const getNameFromUrl = () => {
    let retVal = '';
    let url_array = window.location.pathname.slice(1).slice(0, -1).split('/');
    if (url_array.length > 0) {
        retVal = url_array[url_array.length - 1];
        retVal = retVal.replaceAll('.html', '').replaceAll('.htm', '');
        retVal = retVal.charAt(0).toUpperCase() + retVal.slice(1);
        if (retVal.length > 8)
            retVal = retVal.replaceAll('-content', '');
    }
    return retVal;
};

const getL2NameFromUrl = () => {
    let retVal = '';
    let url_array = window.location.pathname.slice(1).slice(0, -1).split('/');
    if (url_array.length > 0) {
        if (url_array.length < 2)
            retVal = url_array[url_array.length - 1];
        else
            retVal = url_array[url_array.length - 2] + '-' + url_array[url_array.length - 1];
        retVal = retVal.replaceAll('.html', '').replaceAll('.htm', '');
        retVal = retVal.charAt(0).toUpperCase() + retVal.slice(1);
        if (retVal.length > 8)
            retVal = retVal.replaceAll('-content', '');
    }
    return retVal;
};


