const triggerTealiumAnalyticsAsynch = (context) => {
    return new Promise((resolve, reject) => {
        let maxChecks = 30;
        let currentChecks = 0;
        let tagCheckInterval = window.setInterval(function() {
            if (currentChecks === maxChecks) {
                window.clearInterval(tagCheckInterval);
                reject(new Error("No tealium tag, waited max amount of time"));
            }
            currentChecks++;
            if (window.utag !== undefined) {
                window.clearInterval(tagCheckInterval);
                resolve(triggerTealiumAnalytics(context));
            }
        }, 250);
    });
}

