    setScreen: function(screen_name) {
      var elements = this.parentElement.querySelectorAll('*[data-screen]');
      for (var i = 0; i < elements.length; i++) {
        var screen = elements[i].getAttribute('data-screen');
        if (screen != '' && screen != screen_name) {
          elements[i].classList.add('screen-hidden');
        } else {
          elements[i].classList.remove('screen-hidden');
        }
      }
    },

