class SpSlider
{
    constructor(selector)
    {
        this.container  = document.querySelector(selector);
        if (!this.container)
            throw new Error('Container with selector `'+ selector + '` not found');


        this.leftButton = document.createElement('span');
        this.leftButton.id          = 'slider-scroll-left';
        this.leftButton.className   = 'slider left';
        this.container.parentNode.appendChild(this.leftButton);

        this.rightButton    = document.createElement('span');
        this.rightButton.id         = 'slider-scroll-right';
        this.rightButton.className  = 'slider right';
        this.container.parentNode.appendChild(this.rightButton);

        const   lb = document.createElement('span');
                lb.className   = 'slider left mobile';
        this.container.parentNode.appendChild(lb);

        const   rb = document.createElement('span');
                rb.className  = 'slider right mobile';
        this.container.parentNode.appendChild(rb);
        
        

        this.leftButton.addEventListener('click', e => this.clickLeft(e));
        this.rightButton.addEventListener('click', e => this.clickRight(e));

        this.blockMargin    = this.cards[1].offsetLeft - this.cards[0].offsetLeft - this.cardWidth;
        this.cardPosition   = 0;
        this.time           = 10;

        this.processSlide   = 0;


        let touchSlide = 0;

        const parentWidth       = this.container.parentNode.clientWidth;
        let cardsPerScreen    = (parentWidth / this.cardWidth <= 1) ?
            parentWidth / this.cardWidth :
            parseInt(parentWidth / this.cardWidth);

        let preWidth = parentWidth / this.cardWidth;

        if (preWidth >= 1.5 && preWidth <= 2)
            cardsPerScreen = 2;

        if (preWidth >= 2.5 && preWidth <= 3)
            cardsPerScreen = 3;

        const newSize = (cardsPerScreen >= 1) ?
            (parentWidth / cardsPerScreen) - this.blockMargin:
            parentWidth * cardsPerScreen - this.blockMargin;

        //console.log(this.container.offsetLeft, this.blockMargin , parentWidth, cardsPerScreen, newSize);

        Array.from(this.cards).forEach(card => card.style.width = card.style.minWidth= newSize + 'px' );

        // do not use slides for mobile
        /*
        this.container.addEventListener('touchstart', e =>
        {
            touchSlide = this.getTouches(e).clientX;
        });

        this.container.addEventListener('touchmove',  e =>
        {
            const slideDiff = touchSlide - this.getTouches(e).clientX;

            if (this.processSlide)
                return;

            if ( slideDiff > 0 )
                this.clickRight(e);
            else
                this.clickLeft(e);
        });        
         */
    }

    clickRight(event)
    {
        if (this.processSlide)
            return;

        this.processSlide = 1;

        const   step = this.cardWidth / this.time;
        let     current = 0,
            position = this.container.style.marginLeft ?
                parseInt(this.container.style.marginLeft.replace('px','')) : 0;

        if (!this.checkLimit())
        {
            this.processSlide = 0;
            return;
        }

        this.cardPosition --;


        const tm = setInterval(() =>
        {
            current ++;
            position -= step;

            this.container.style.marginLeft = position+'px';
            if (current >= this.time)
            {
                this.container.style.marginLeft = (position - this.blockMargin ) +'px';
                clearInterval(tm);
                current = 0;
                this.processSlide = 0;
            }
        }, this.time);
    }

    clickLeft(event)
    {
        if (this.processSlide)
            return

        this.processSlide = 1;

        const   step    = this.cardWidth / this.time;
        let     current = 0,
            position = this.container.style.marginLeft ?
                parseInt(this.container.style.marginLeft.replace('px','')) : 0;

        this.cardPosition ++;

        if (this.cardPosition > 0)
        {
            this.cardPosition = 0;
            this.processSlide = 0;
            return;
        }

        const tm = setInterval(() =>
        {
            current ++;
            position += step;

            this.container.style.marginLeft = position+'px';
            if (current >= this.time)
            {
                this.container.style.marginLeft = (position + this.blockMargin ) +'px';
                clearInterval(tm);
                current = 0;
                this.processSlide = 0;
            }
        }, this.time);
    }

    get cards()
    {
        return this.container.getElementsByTagName('li');
    }

    get cardWidth()
    {
        return this.cards[0].clientWidth;
    }

    checkLimit()
    {
        const parentWidth       = this.container.parentNode.clientWidth;
        const cardsPerScreen    = parentWidth / this.cardWidth;


        if (Math.abs(this.cardPosition) > this.cards.length - cardsPerScreen)
            return false;

        return true;
    }

    getTouches(event)
    {
        const touches = event.touches;
        if (!touches)
            throw new Error('Unable to get touch event');

        return touches[0];
    }
}

