function setGradient(image) {

    if(!this.CT)
        this.CT = new ColorThief();
    //console.log('set gradient');
    var base_color = this.CT.getColor(image);
    var rgb = base_color[0] + ',' + base_color[1] + ',' + base_color[2];

    var start_color = 'rgba(' + rgb + ',0) 0%';
    var end_color = 'rgba(' + rgb + ',1) 100%';

    var gradient_direction = 'to bottom';
    //if(div.classList.contains('svk-unit-image-gradient-left'))
        //gradient_direction = 'to left';

    var gradient_style =
    'background: -moz-linear-gradient('+gradient_direction+', '+start_color+', '+end_color+');' +
    'background: -webkit-linear-gradient('+gradient_direction+', '+start_color+', '+end_color+');' +
    'background: linear-gradient('+gradient_direction+', '+start_color+', '+end_color+');';
    //'filter: progid:DXImageTransform.Microsoft.gradient( startColorstr="#00000000", endColorstr="#22202b",GradientType=0 );';
    //

    var grad = document.createElement("div");
	grad.setAttribute('class', "article-image-gradient"); 
	grad.setAttribute('style', gradient_style); 
    image.parentNode.insertBefore(grad, image.nextSibling);

    image.parentNode.classList.add('has-gradient-inside');

    image.closest('li.article').style.backgroundColor = 'rgb('+rgb+')';

    var image_color_type = 'svk-image-type-dark';
    var avg_color = (base_color[0] + base_color[1] + base_color[2]) / 3;
    if(avg_color >=128)
        image_color_type = 'svk-image-type-light';

    image.closest('li.article').classList.add(image_color_type);
};

