function calculateTotalSession(){const desktopSessionValue=parseFloat(document.getElementById("desktop-session").value)||0;const tabletSessionValue=parseFloat(document.getElementById("tablet-session").value)||0;const mobileSessionValue=parseFloat(document.getElementById("mobile-session").value)||0;document.getElementById("total-sessions").value=desktopSessionValue+tabletSessionValue+mobileSessionValue;}
