function findMessageSource(event)
{
	var sourceFrame = null;
	var allFrames = document.getElementsByTagName("IFRAME");
	for (var i=0; i<allFrames.length; i++) {
		var f = allFrames[i];
		if(f.contentWindow==event.source) {
			sourceFrame = f;
			break;
		}
	}
	return sourceFrame;
}

