function receiveMessage(event)
{
	var msg = event.data;
	switch(msg.type) {
        case 'pageupdate':
			if(!this.pageUpdated) {
                ajax.get('main/register-visit', {
                    'w': WIDGET_ID,
                    'url': msg.data.url
                }, function(response){
                    PAGE_ID=response;
                    console.log('For url "' + msg.data.url + '" found page #' + PAGE_ID);
                });
                this.pageUpdated=true;
            }
        break;
		case 'register':
			FrameHeightManager.frameId = msg.data.frameId;
			FrameHeightManager.FrameWindow = event.source;
			FrameHeightManager.publishHeight();
			registerLoad(msg.data.lt);
			//setImages();
		break;
		case 'inView':
			if(!this.inViewSent) {
					this.inViewSent = true;
					ajax.post('main/register-view?w=' + WIDGET_ID + '&page_id=' + PAGE_ID,
							{
								'_csrf-widget': 'qOU04NS2MU5f0Js_ec7ZR40kn5onR4WoJiYmVXURIEPh0nmKsdsAAy6_00cOi7Mpwk7Y00YixNt0QlUAQWB3FA==',
                                'aids': getAids(),
                                'delnots': getViewDelNot(),
                                'bcs': getViewBC(),
                                'rtb_stats': JSON.stringify(getRtbStats())
							}, function(){});

					document.dispatchEvent(new Event('svk-widget-view'));

                    //setImages();

                    document.body.addEventListener('touchstart', function(){});
			}

		break;

		case 'SVK-Native-resize_embed':
            var new_height = msg.data;
			if(!new_height)
				return;

            new_height = (new_height).toString() + 'px';
            var iframe = findMessageSource(event);
            if(iframe) {
                iframe.style.maxHeight = new_height;
                var css = iframe.style.cssText;
                css = css.replace(/height: (\d+)px(.*?);/gi, 'height: ' + new_height + ' !important;');
                iframe.style.cssText = css;

				iframe.parentElement.querySelector('a').remove();
				var aid = iframe.getAttribute('data-aid');
				iframe.parentElement.setAttribute('data-aid', aid);
            }
		break;

        case 'resize_big':
            var new_height = (msg.data).toString() + 'px';
            var iframe = document.getElementById('rtb_big');
            if(iframe) {
                iframe.style.maxHeight = new_height;
                var css = iframe.style.cssText;
                css = css.replace(/height: (\d+)px(.*?);/gi, 'height: ' + new_height + ' !important;');
                iframe.style.cssText = css;
            }
        break;
        case 'scrolling':
            var unscrolled = msg.data;
            if(unscrolled < 500)
                setImages();
        break;
		//default:
			//console.log('Unknown message type: ' + msg.type );
	}
}

var ajax = {};
ajax.x = function () {
    if (typeof XMLHttpRequest !== 'undefined') {
        return new XMLHttpRequest();
    }
    var versions = [
        "MSXML2.XmlHttp.6.0",
        "MSXML2.XmlHttp.5.0",
        "MSXML2.XmlHttp.4.0",
        "MSXML2.XmlHttp.3.0",
        "MSXML2.XmlHttp.2.0",
        "Microsoft.XmlHttp"
    ];

    var xhr;
    for (var i = 0; i < versions.length; i++) {
        try {
            xhr = new ActiveXObject(versions[i]);
            break;
        } catch (e) {
        }
    }
    return xhr;
};

ajax.send = function (url, callback, method, data, async) {
    if (async === undefined) {
        async = true;
    }
    var x = ajax.x();
    x.open(method, url, async);
    x.onreadystatechange = function () {
        if (x.readyState == 4) {
            callback(x.responseText)
        }
    };
    if (method == 'POST') {
        x.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');
    }
    x.send(data)
};

ajax.get = function (url, data, callback, async) {
    var query = [];
    for (var key in data) {
        query.push(encodeURIComponent(key) + '=' + encodeURIComponent(data[key]));
    }
    ajax.send(url + (query.length ? '?' + query.join('&') : ''), callback, 'GET', null, async)
};

ajax.post = function (url, data, callback, async) {
    var query = [];
    for (var key in data) {
        query.push(encodeURIComponent(key) + '=' + encodeURIComponent(data[key]));
    }
    ajax.send(url, callback, 'POST', query.join('&'), async)
};


