function initUnitViewSystem() {

    const unitIntersection = function(entries) {
        var i = 0;
        entries.forEach((entry)=>{
            var ratio = entry.intersectionRatio;
            i++;
            if(ratio >= 0.75) {
                unitView(entry.target);
            }
        });
    };
    const observer = new IntersectionObserver(function(entries){unitIntersection(entries);}, {
        threshold: 0.75,
        root: null
    });

    document.querySelectorAll('li.unit').forEach((unit)=>{ // li.article
        try {
            observer.observe(unit);
        } catch (e) {
            return false;
        }
    });
}

