                                    <div class="input ml10">
                                        <input class="flat spreedly-input-template" type="text" name="postal_code" placeholder="{{'Postal code' | t}}"
                                               ng-model="input.zipCode"
                                               required/>
                                        <input type="text" class="flat spreedly-input-color-template hidden">
                                    </div>
                                </div>
